fsw_signs = (swu2fsw(sign) for sign in text.split())

print("flowchart TD")

symbols = set()
for sign in signs:
    for symbol in sign['symbols']:
        symbols.add(f"{symbol['symbol']}[{fsw2swu(symbol['symbol'])}]")

for symbol in symbols:
    print("    ", symbol)

for sign in signs:
    for symbol1 in sign['symbols']:
        for symbol2 in sign['symbols']:
            if symbol1['symbol'] != symbol2['symbol']:
                # arrow is always from left to right
                if symbol1['position'][0] > symbol2['position'][0]:
                    relative_position_x = symbol2['position'][0] - symbol1['position'][0]
                    relative_position_y = symbol2['position'][1] - symbol1['position'][1]
                    print("    ",
                          f"{symbol1['symbol']} <-- {relative_position_x}/{relative_position_y} --> {symbol2['symbol']}")
